import React, { useState, useEffect } from 'react';
import { Factory, EasyScore, System } from 'vexflow';

const MusicSheetWithApi = () => {
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    const fetchNotesFromAPI = async () => {
      // Replace 'your-api-endpoint' with the actual API endpoint to fetch notes
      const response = await fetch('http://localhost:5000/get_note_data');
      const data = await response.json();
      setNotes(data.notes);
    };

    fetchNotesFromAPI();
  }, []);

  useEffect(() => {
    const renderMusicSheet = () => {
      const vf = new Factory({
        renderer: { elementId: 'output', width: 500, height: 200 },
      });

      const score = vf.EasyScore();
      const system = vf.System();

      const voice = score.voice(score.notes(notes.join(', ')));

      system
      .addStave({
        voices: [
          score.voice(score.notes('C#5/q, B4, A4, G#4')),
        ],
      })
      .addClef('treble')
      .addTimeSignature('4/4');

    vf.draw();
    };

    renderMusicSheet();
  }, [notes]);

  return <div id="output"></div>;
};

export default MusicSheetWithApi;
