import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Score } from './Score';
import BsNav from '../Containers/BsNav';
import '../styles/MusicVisualiser.css';
import io from 'socket.io-client';

const MusicVisualiser = () => {
  const [noteData, setNoteData] = useState([]);
  const apiUrl = process.env.REACT_APP_API_BASE_URL;
  const limit = 16;

  useEffect(() => {
    const fetchData = async () => {
      // Initial fetch logic remains unchanged
    };
    fetchData();

    const socket = io(apiUrl); // Connect to the WebSocket server
    socket.on('new_note', (data) => {
      setNoteData(currentData => [data.note_data, ...currentData].slice(0, limit));
    });

    return () => socket.disconnect(); // Clean up on component unmount
  }, [apiUrl]);

  return (
    <div className="MainPage-page">
      <BsNav />
      <div className="centered-content">
        <div className="score-container">
          <Score className="Score" staves={noteData} />
        </div>
      </div>
    </div>
  );
};

export default MusicVisualiser;
