import React, { useState, useEffect } from 'react';
import BsNav from '../Containers/BsNav';

const MusicalStairsGame = () => {
  const allTasks = [
    'Major triad', 
    'Perfect 5th', 'Major 3rd', 
    'Perfect 4th', 'Major 2nd', 
,
  ];

  const [completedTasks, setCompletedTasks] = useState([]);
  const [currentTask, setCurrentTask] = useState('');
  const [score, setScore] = useState(0);
  const [gameOver, setGameOver] = useState(false);

  useEffect(() => {
    generateRandomTask();
  }, []); // Empty dependency array ensures this effect runs once on mount

  const generateRandomTask = () => {
    if (completedTasks.length === allTasks.length) {
      // All tasks have been completed, set game over
      setGameOver(true);
      return;
    }

    let remainingTasks = allTasks.filter(task => !completedTasks.includes(task));
    const randomIndex = Math.floor(Math.random() * remainingTasks.length);
    const newTask = remainingTasks[randomIndex];

    // Update the completed tasks list
    setCompletedTasks((prevTasks) => [...prevTasks, newTask]);

    setCurrentTask(newTask);
  };

  const handleTaskCompleted = () => {
    // You can customize the scoring logic here
    setScore(score + 1);
    generateRandomTask(); // Generate a new task after completion
  };

  // Optionally, you can add a function to reset the game
  const resetGame = () => {
    setCompletedTasks([]);
    setScore(0);
    setGameOver(false);
    generateRandomTask();
  };

  return (
    <div>
      <BsNav />
      <h1>Play the Musical Stairs Game</h1>
      <p>Play the following chord or interval on the stairs: {currentTask}</p>
      <p>Score: {score}</p>
      {gameOver ? (
        <div>
          <p>Game Over! Your score: {score} out of {completedTasks.length}</p>
          <button onClick={resetGame}>Restart Game</button>
        </div>
      ) : (
        <button onClick={handleTaskCompleted}>Generate New Task</button>
      )}
      {/* Add your stair visualization or other game elements here */}
    </div>
  );
};

export default MusicalStairsGame;
