import React, { useState } from 'react';
import BsNav from '../Containers/BsNav'; // Import the BasicExample component
import '../styles/mainPage.css'; // Create a CSS file for styling


const InstrumentSelector = () => {
  const [activeDictionary, setActiveDictionary] = useState('topic_to_audio');

  const switchDictionary = (newDictionary) => {
    // Make a POST request to your Flask app to switch the dictionary
    fetch('http://localhost:5000/switch_dictionary', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ dictionary: newDictionary }),
    })
      .then((response) => response.json())
      .then((data) => {
        // Update the active dictionary in the React component state
        setActiveDictionary(newDictionary);
        console.log(`Switched to ${newDictionary}`);
      })
      .catch((error) => {
        console.error('Error switching dictionary:', error);
      });
  };

  return (
    <div>
        {/* Use the BasicExample component as the new navigation bar */}
      <BsNav />
      <h2>Active Dictionary: {activeDictionary}</h2>
      <button onClick={() => switchDictionary('topic_to_audio')}>Switch to Dictionary 1</button>
      <button onClick={() => switchDictionary('topic_to_audio2')}>Switch to Dictionary 2</button>
      <button onClick={() => switchDictionary('topic_to_audio3')}>Switch to Dictionary 3</button>
      <button onClick={() => switchDictionary('topic_to_audio4')}>Switch to Dictionary 4</button>
    </div>
  );
};

export default InstrumentSelector;
