import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { getSocket } from './SocketService'; // Adjust the import path as necessary
import { Score } from './Score';
import BsNav from '../Containers/BsNav';
import '../styles/MusicVisualiser.css';

const MusicVisualiser = () => {
  const [noteData, setNoteData] = useState([]);
  const apiUrl = process.env.REACT_APP_API_BASE_URL;


  useEffect(() => {
    const socket = getSocket(); // Use the singleton socket instance

    const fetchData = async () => {
      try {
        const response = await axios.get(`${apiUrl}/get_note_data?limit=16`);
        setNoteData(response.data);
        console.log('Initial note data fetched');
      } catch (error) {
        console.error('Error fetching note data:', error);
      }
    };
    fetchData();


    socket.on('notes_update', (data) => {
      console.log('Received new note data via WebSocket:', data);
      setNoteData(data);
    });

    // No need to disconnect in the cleanup function as we want the socket to persist
    return () => {
      socket.off('notes_update');
    };
  }, [apiUrl]);

  return (
    <div className="MainPage-page">
      <BsNav />
      <div className="centered-content">
        <div className="score-container">
          <Score className="Score" staves={noteData} />
        </div>
      </div>
    </div>
  );
};

export default MusicVisualiser;
