import React, { useState, useEffect, useRef } from 'react';
import BsNav from '../Containers/BsNav';
import Vex from 'vexflow';

const MusicalStairsGame = () => {
  const allTasks = [
    { name: 'Major 3rd', notes: 'E4, G4' }, // Use VexFlow note string format
    { name: 'Perfect 4th', notes: 'D4, G4' },
    { name: 'Major 2nd', notes: 'C4, D4' },
    { name: 'Perfect 5th', notes: 'C4, G4' },
  ];

  const [completedTasks, setCompletedTasks] = useState([]);
  const [currentTask, setCurrentTask] = useState({});
  const [score, setScore] = useState(0);
  const [gameOver, setGameOver] = useState(false);
  const canvasRef = useRef(null);

  useEffect(() => {
    generateRandomTask();
  }, []);

  useEffect(() => {
    if (!currentTask.notes) return; // Don't draw if no task is selected

    const VF = Vex.Flow;
    const canvas = canvasRef.current;
    const renderer = new VF.Renderer(canvas, VF.Renderer.Backends.CANVAS);
    const context = renderer.getContext();
    context.setFont("Arial", 10, "").setBackgroundFillStyle("#eed");

    const stave = new VF.Stave(10, 40, 400);
    stave.addClef("treble").addTimeSignature("4/4");
    stave.setContext(context).draw();

    // Splitting notes string into an array, then mapping each to create a StaveNote
    let notesArray = currentTask.notes.split(', ').map(noteString => {
        // Here, each noteString is something like "C4", "D4", etc.
        // For VexFlow, we need to specify the duration as well. Let's assume quarter notes for all.
        return new VF.StaveNote({ keys: [noteString], duration: "q" });
    });

    let voice = new VF.Voice({num_beats: 4, beat_value: 4});
    voice.addTickables(notesArray);

    // Formatting and drawing the notes
    new VF.Formatter().joinVoices([voice]).format([voice], 400);
    voice.draw(context, stave);
}, [currentTask]);




  const generateRandomTask = () => {
    if (completedTasks.length === allTasks.length) {
      setGameOver(true);
      return;
    }

    let remainingTasks = allTasks.filter(task => !completedTasks.find(ct => ct.name === task.name));
    const randomIndex = Math.floor(Math.random() * remainingTasks.length);
    const newTask = remainingTasks[randomIndex];

    setCompletedTasks(prevTasks => [...prevTasks, newTask]);
    setCurrentTask(newTask);
  };

  const handleTaskCompleted = () => {
    setScore(score + 1);
    generateRandomTask();
  };

  const resetGame = () => {
    setCompletedTasks([]);
    setScore(0);
    setGameOver(false);
    generateRandomTask();
  };

  return (
    <div>
      <BsNav />
      <h1>Play the Musical Stairs Game</h1>
      <p>Play the following chord or interval on the stairs: {currentTask.name}</p>
      <canvas ref={canvasRef} width={500} height={200} />
      <p>Score: {score}</p>
      {gameOver ? (
        <div>
          <p>Game Over! Your score: {score} out of {completedTasks.length}</p>
          <button onClick={resetGame}>Restart Game</button>
        </div>
      ) : (
        <button onClick={handleTaskCompleted}>Complete Task & Generate New Task</button>
      )}
    </div>
  );
};

export default MusicalStairsGame;
