import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BsNav from '../Containers/BsNav';

const MusicalStairsGame = () => {
  const allTasks = [
    { name: 'Perfect 5th', notes: ['C4', 'G4'] },
    { name: 'Major 3rd', notes: ['C4', 'E4'] },
    { name: 'Perfect 4th', notes: ['C4', 'F4'] },
    { name: 'Major 2nd', notes: ['C4', 'D4'] },
  ];

  const [completedTasks, setCompletedTasks] = useState([]);
  const [currentTask, setCurrentTask] = useState(null);
  const [score, setScore] = useState(0);
  const [gameOver, setGameOver] = useState(false);

  useEffect(() => {
    generateRandomTask();
  }, []);

  const generateRandomTask = () => {
    if (completedTasks.length === allTasks.length) {
      setGameOver(true);
      return;
    }

    let remainingTasks = allTasks.filter(task => !completedTasks.includes(task));
    const randomIndex = Math.floor(Math.random() * remainingTasks.length);
    const newTask = remainingTasks[randomIndex];

    setCurrentTask(newTask);
    setCompletedTasks(prevTasks => [...prevTasks, newTask]);
  };

  const validateTask = async () => {
    // Validation logic as previously discussed
  };

  // Define the resetGame function here
  const resetGame = () => {
    setCompletedTasks([]);
    setCurrentTask(null); // Or set to an initial state if preferred
    setScore(0);
    setGameOver(false);
    generateRandomTask(); // Start a new task immediately
  };

  return (
    <div>
      <BsNav />
      <h1>Play the Musical Stairs Game</h1>
      {currentTask ? (
        <>
          <p>Play the following chord or interval on the stairs: {currentTask.name}</p>
          <button onClick={validateTask}>Validate Task</button>
        </>
      ) : (
        <p>Loading task...</p>
      )}
      <p>Score: {score}</p>
      {gameOver && (
        <div>
          <p>Game Over! Your score: {score} out of {allTasks.length}</p>
          <button onClick={resetGame}>Restart Game</button>
        </div>
      )}
    </div>
  );
};

export default MusicalStairsGame;
