import React, { useState } from 'react';
import axios from 'axios';
import BsNav from '../Containers/BsNav';
import '../styles/MusicalStairsGame.css';

const MusicalStairsGame = () => {
  // Define your tasks and associated notes here.
  const allTasks = [
    { name: 'Perfect 5th', notes: ['C4', 'G4'] },
    { name: 'Major 3rd', notes: ['C4', 'E4'] },
    { name: 'Perfect 4th', notes: ['C4', 'F4'] },
    { name: 'Major 2nd', notes: ['C4', 'D4'] },
  ];

  const [completedTasks, setCompletedTasks] = useState([]);
  const [currentTask, setCurrentTask] = useState(null);
  const [score, setScore] = useState(0);
  const [gameOver, setGameOver] = useState(false);

  // Function to select a new task randomly.
  const generateRandomTask = () => {
    // Filter out tasks that have already been completed
    let remainingTasks = allTasks.filter(task => !completedTasks.includes(task.name));

    if (remainingTasks.length === 0) {
      // All tasks have been presented
      setGameOver(true);
      return;
    }

    const randomIndex = Math.floor(Math.random() * remainingTasks.length);
    const newTask = remainingTasks[randomIndex];

    setCurrentTask(newTask);
    // Update completedTasks to include the name of the new task
    setCompletedTasks(prevTasks => [...prevTasks, newTask.name]);
  };


  // Function to fetch note data and validate the current task.
  const validateTask = async () => {
    try {
      const response = await axios.get('http://localhost:5000/get_note_data', {
        params: { limit: 2 } // Fetching the last 2 note entries for validation.
      });
      const fetchedNotes = response.data; // Assuming this returns an array of note groups like [["C4", "G4"]]

      // Updated validation logic
      const isValid = fetchedNotes.some(group => {
        const taskNotesStr = currentTask.notes.sort().join(',');
        const groupStr = group.sort().join(',');
        return taskNotesStr === groupStr;
      });

      console.log("Fetched Notes:", fetchedNotes);
      console.log("Is Valid:", isValid);

      if (isValid) {
        alert("Correct! Moving to the next task.");
        setScore(prevScore => prevScore + 1); // Increment score only if valid
      } else {
        alert("Incorrect. Moving to the next task.");
      }
      generateRandomTask(); // Move to the next task regardless of validation outcome
    } catch (error) {
      console.error("Error fetching note data:", error);
    }
  };


  // Initial task generation upon component mount.
  useState(() => {
    generateRandomTask();
  }, []);

  return (
    <div className="musical-stairs-game"> {/* Ensure this class aligns with your GoDaddy-like CSS */}
      <BsNav />
      <div className="game-container"> {/* Consider this for additional styling if needed */}
        <h1>Play the Musical Stairs Game</h1>
        {currentTask ? (
          <>
            <p>Play the following chord or interval on the stairs: {currentTask.name}</p>
            <button className="validate-task-btn" onClick={validateTask}>Validate Task</button>
          </>
        ) : (
          <p>Loading task...</p>
        )}
        <p>Score: {score}</p>
        {gameOver && (
          <div className="game-over-container">
            <p>Game Over! Your score: {score} out of {allTasks.length}</p>
            <button className="restart-game-btn" onClick={() => generateRandomTask()}>Restart Game</button>
          </div>
        )}
      </div>
    </div>
  );
};
export default MusicalStairsGame;
