import React, { useRef, useEffect } from 'react';
import VexFlow from 'vexflow';

const VF = VexFlow.Flow;
const { Formatter, Renderer, Stave, StaveNote } = VF;

const clefAndTimeWidth = 60;
const notesDuration = 'q'; // Default duration for all notes

export function Score({
  staves = [],
  clef = 'treble',
  timeSignature = '4/4',
  width = 500,
  height = 150,
}) {
  const container = useRef();
  const rendererRef = useRef();

  const barsPerLine = 8;
  const staveWidth = (width - clefAndTimeWidth) / barsPerLine;

  useEffect(() => {
    if (rendererRef.current == null) {
      rendererRef.current = new Renderer(container.current, Renderer.Backends.SVG);
    }

    const renderer = rendererRef.current;
    renderer.resize(width, height);
    const context = renderer.getContext();
    context.setFont('Arial', 10, '').setBackgroundFillStyle('#eed');

    let currX = 0;
    let currStaveIndex = 0;
    let currBar = 0;

    staves.forEach((notes) => {
      if (currBar % barsPerLine === 0) {
        currX = currBar === 0 ? 0 : clefAndTimeWidth + (currBar / barsPerLine - 1) * staveWidth;
        const currStave = new Stave(currX, 0, staveWidth);

        if (currBar === 0) {
          currStave.setWidth(staveWidth + clefAndTimeWidth);
          currStave.addClef(clef).addTimeSignature(timeSignature);
        }

        currStave.setContext(context).draw();
      }

      const processedNotes = notes.map((pitch) => {
        const [note, octave] = pitch.match(/[a-zA-Z]+|[0-9]+/g);
        return new StaveNote({
          keys: [`${note}/${octave}`],
          duration: notesDuration,
        });
      });

      const currStave = new Stave(currX, 0, staveWidth);
      currStave.setContext(context);

      Formatter.FormatAndDraw(context, currStave, processedNotes, {
        auto_beam: true,
      });

      currX += staveWidth;
      currBar += 1;
    });
  }, [staves, clef, timeSignature, width, height, staveWidth]);

  return <div ref={container} />;
}
