import React, { useEffect, useState } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import { Score } from './Score';
import BsNav from '../Containers/BsNav';
import '../styles/MusicVisualiser.css';

const MusicVisualiser = () => {
  const [noteData, setNoteData] = useState([]);
  const apiUrl = process.env.REACT_APP_API_BASE_URL;
  const wsUrl = 'http://localhost:5000'; // Adjust if your server is running on a different address

  useEffect(() => {
    // Initial fetch of note data
    const fetchData = async () => {
      try {
        const response = await axios.get(`${apiUrl}/get_note_data?limit=16`);
        setNoteData(response.data);
        console.log('Initial note data fetched');
      } catch (error) {
        console.error('Error fetching note data:', error);
      }
    };

    console.log('Attempting to connect to WebSocket server');
    const socket = io(wsUrl);

    socket.on('connect', () => {
      console.log('Connected to WebSocket server');
      fetchData(); // Fetch data once connected to ensure fresh data
    });

    socket.on('notes_update', (data) => {
      console.log('Received new note data via WebSocket:', data);
      setNoteData(data); // Update state with new data
    });

    socket.on('disconnect', (reason) => {
      console.log('Disconnected from WebSocket server:', reason);
    });

    // Clean up function to disconnect the socket when the component unmounts
    return () => {
      console.log('Disconnecting from WebSocket server');
      socket.disconnect();
    };
  }, [apiUrl, wsUrl]); // Dependencies

  return (
    <div className="MainPage-page">
      <BsNav />
      <div className="centered-content">
        <div className="score-container">
          <Score className="Score" staves={noteData} />
        </div>
      </div>
    </div>
  );
};

export default MusicVisualiser;
