import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Score } from './Score';
import BsNav from '../Containers/BsNav';

const MusicVisualiser = () => {
  const [noteData, setNoteData] = useState([]);
  const apiUrl = process.env.REACT_APP_API_BASE_URL;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`${apiUrl}/get_note_data`);
        // Assuming the response data is an array of entries
        const data = response.data;
        // Get the last 16 entries
        const lastSixteenEntries = data.length > 8 ? data.slice(-8) : data;
        setNoteData(lastSixteenEntries);
        console.log('Note data fetched:', lastSixteenEntries);
      } catch (error) {
        console.error('Error fetching note data:', error);
      }
    };

    fetchData(); // Call the fetchData function when the component mounts
  }, [apiUrl]); // The empty dependency array ensures that this effect runs only once on mount

  return (
    <div className="MainPage-page">
      <BsNav />
      <div className="centered-content">
        <Score staves={noteData} />
      </div>
    </div>
  );
};

export default MusicVisualiser;
