import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BsNav from '../Containers/BsNav';
import '../styles/MusicalStairsGame.css'; // Ensure this path matches your CSS file's location

const MusicalStairsGame = () => {
  const allTasks = [
    { name: 'Perfect 5th', notes: ['C4', 'G4'] },
    { name: 'Major 3rd', notes: ['C4', 'E4'] },
    { name: 'Perfect 4th', notes: ['C4', 'F4'] },
    { name: 'Major 2nd', notes: ['C4', 'D4'] },
  ];

  const [completedTasks, setCompletedTasks] = useState([]);
  const [currentTask, setCurrentTask] = useState(null);
  const [score, setScore] = useState(0);
  const [gameOver, setGameOver] = useState(false);

  const generateRandomTask = () => {
    let remainingTasks = allTasks.filter(task => !completedTasks.includes(task.name));
    if (remainingTasks.length === 0) {
      setGameOver(true);
      return;
    }
    const randomIndex = Math.floor(Math.random() * remainingTasks.length);
    setCurrentTask(remainingTasks[randomIndex]);
    setCompletedTasks(prev => [...prev, remainingTasks[randomIndex].name]);
  };

  const validateTask = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_BASE_URL}/get_note_data`, { params: { limit: 2 } });
      const isValid = response.data.some(group => group.sort().join(',') === currentTask.notes.sort().join(','));
      isValid ? setScore(score + 1) : alert("Try again!");
      generateRandomTask();
    } catch (error) {
      console.error("Validation error:", error);
    }
  };

  useEffect(() => {
    generateRandomTask();
  }, []);

  return (
    <div className="game-container">
      <BsNav />
      <h1 className="game-title">Musical Stairs Game</h1>
      {gameOver ? (
        <div className="game-over">
          <p>Game Over! Your final score is {score} out of {allTasks.length}.</p>
          <button className="restart-button" onClick={() => window.location.reload()}>Restart Game</button>
        </div>
      ) : (
        <div className="task-container">
          {currentTask && (
            <>
              <p className="task-instruction">Current task: Play the {currentTask.name}</p>
              <button className="validate-button" onClick={validateTask}>Validate</button>
            </>
          )}
          <p className="score">Score: {score}</p>
        </div>
      )}
    </div>
  );
};

export default MusicalStairsGame;
