import React from 'react';
import { Stave, Voice, Formatter, Renderer } from 'vexflow';

const Score = ({ staves }) => {
  const containerRef = React.useRef();

  React.useEffect(() => {
    // Clear the container before rendering new staves
    containerRef.current.innerHTML = '';

    staves.forEach((notes, index) => {
      if (!Array.isArray(notes)) {
        // Log a warning or handle the non-array case
        console.warn(`Stave ${index + 1} does not contain an array of notes: ${notes}`);
        return;
      }

      const vf = new VF.Factory({ renderer: { elementId: `vf-container-${index}`, width: 500, height: 200 } });
      const score = vf.EasyScore();
      const system = vf.System();

      const stave = new VF.Stave(10, 40 + index * 100, 500);
      system.addStave({ voices: [score.voice(score.notes(...notes))] });

      vf.draw();
    });
  }, [staves]);

  return <div ref={containerRef}></div>;
};

export default Score;
