import React, { useEffect, useState } from 'react';
import Vex from 'vexflow';

function MusicVisualizer() {
  const [noteData, setNoteData] = useState([]);

  useEffect(() => {
    // Fetch note data from your database (replace with your actual API call)
    // Example structure: { pitch: 'C4', duration: '4n' }
    fetch('http://localhost:5000/get_note_data')
      .then((response) => response.json())
      .then((data) => {
        setNoteData(data);
      })
      .catch((error) => {
        console.error('Error fetching note data:', error);
      });
  }, []);

// ... (previous code)

useEffect(() => {
  // Create an instance of VexFlow
  const VF = Vex.Flow;
  const container = document.getElementById('visualizer-container');
  container.innerHTML = ''; // Clear previous content

  // Create a renderer and context
  const renderer = new VF.Renderer(container, VF.Renderer.Backends.SVG);
  const context = renderer.getContext();

  // Create a stave
  const stave = new VF.Stave(10, 0, 400);

  // Create a voice
  const voice = new VF.Voice({ num_beats: 4, beat_value: 4 });

  // Create an array to store valid stave notes
  const staveNotes = [];

  noteData.forEach((note) => {
    try {
      if (note && note.pitch) {
        // Check if the pitch is valid
        const validPitchRegex = /^[A-G](b|#)?\d+$/;
        if (!validPitchRegex.test(note.pitch)) {
          console.warn('Invalid pitch:', note.pitch);
        } else {
          const staveNote = new VF.StaveNote({
            keys: [note.pitch],
            duration: 'q',
          });
          staveNote.addModifier(new VF.Annotation(note.pitch).setVerticalJustification(VF.Annotation.VerticalJustify.BOTTOM));
          staveNotes.push(staveNote);
        }
      } else {
        console.warn('Invalid note data:', note);
      }
    } catch (error) {
      console.error('Error creating StaveNote:', error);
      console.log('Note data:', note);
    }
  });

  // Add notes to the voice only if there are enough valid notes
  if (staveNotes.length > 0) {
    console.log('Valid staveNotes:', staveNotes);
    voice.addTickables(staveNotes);

    // Format and draw
    new VF.Formatter().joinVoices([voice]).format([voice], 400);
    stave.setContext(context).draw();
    voice.draw(context, stave);
  }

}, [noteData]);
console.log(noteData)




// ... (remaining code)


  return (
    <div>
      <h1>Music Visualizer</h1>
      <div id="visualizer-container"></div>
    </div>
  );
}

export default MusicVisualizer;
