import React, { useEffect, useState } from 'react';
import Vex from 'vexflow';

function MusicVisualizer() {
  const [noteData, setNoteData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch note data from your Flask server
    fetch('http://localhost:5000/get_note_data')
      .then(response => response.json())
      .then(data => {
        setNoteData(data);
        setLoading(false);
      })
      .catch(error => {
        setError(error);
        setLoading(false);
      });
  }, []); // Empty dependency array to run the effect only once on mount

// ... (previous code)

useEffect(() => {
  if (!loading && !error) {
    console.log('noteData length:', noteData.length);

    // Create an instance of VexFlow
    const VF = Vex.Flow;
    const container = document.getElementById('visualizer-container');
    container.innerHTML = ''; // Clear previous content

    // Check if noteData is not empty
    if (noteData.length > 0) {
      // Create a Vex.Flow Formatter and Voice
      const formatter = new VF.Formatter().joinVoices([new VF.Voice({ time: '4/4' })]);

      // Map noteData to Vex.Flow StaveNotes
      const notes = noteData.map((note) => {
        // Remove quotes from note value
        const noteWithoutQuotes = note.replace(/["']/g, ''); // Remove both single and double quotes
        return new VF.StaveNote({ keys: [noteWithoutQuotes], duration: 'q' });
      });

      // Check if notes array is not empty
      if (notes.length > 0) {
        // Format and draw the notes on the Vex.Flow stave
        formatter.format([new VF.Voice().addTickables(notes)], 400); // Adjust width as needed
        const stave = new VF.Stave(10, 0, 400); // Adjust position and width as needed
        stave.addClef('treble').setContext(container).draw();
        notes.forEach((note) => note.setStave(stave).setContext(container).draw());
      } else {
        console.warn('Note data is empty or invalid.');
      }
    }
  }
}, [loading, error, noteData]);

// ... (rest of the code)


  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <div>
      <h1>Music Visualizer</h1>
      <div id="visualizer-container"></div>
    </div>
  );
}

export default MusicVisualizer;
