import React, { useRef, useEffect } from 'react';
import VexFlow from 'vexflow';
import axios from 'axios'; // Import axios for API requests

const VF = VexFlow.Flow;
const { Formatter, Renderer, Stave, StaveNote } = VF;

const clefAndTimeWidth = 60;

export function Score({
  clef = 'treble',
  timeSignature = '4/4',
  width = 500,
  height = 150,
}) {
  const container = useRef();
  const rendererRef = useRef();

  useEffect(() => {
    // Fetch note data from your API
    axios.get('http://localhost:5000/get_note_data')
      .then(response => {
        const staves = [response.data.notes]; // Assuming response.data.notes is an array of notes
        renderStaves(staves);
      })
      .catch(error => {
        console.error('Error fetching note data:', error);
      });
  }, []);

  const renderStaves = (staves) => {
    if (rendererRef.current == null) {
      rendererRef.current = new Renderer(container.current, Renderer.Backends.SVG);
    }
    const renderer = rendererRef.current;
    renderer.resize(width, height);
    const context = renderer.getContext();
    context.setFont('Arial', 10, '').setBackgroundFillStyle('#eed');
    const staveWidth = (width - clefAndTimeWidth) / staves.length;

    let currX = 0;
    staves.forEach((notes, i) => {
      const stave = new Stave(currX, 0, staveWidth);
      if (i === 0) {
        stave.setWidth(staveWidth + clefAndTimeWidth);
        stave.addClef(clef).addTimeSignature(timeSignature);
      }
      currX += stave.getWidth();
      stave.setContext(context).draw();

      const processedNotes = notes
        .map(note => (typeof note === 'string' ? { key: note } : note))
        .map(note =>
          Array.isArray(note) ? { key: note[0], duration: note[1] } : note
        )
        .map(({ key, ...rest }) =>
          typeof key === 'string'
            ? {
                key: key.includes('/') ? key : `${key[0]}/${key.slice(1)}`,
                ...rest,
              }
            : rest
        )
        .map(
          ({ key, keys, duration = 'q' }) =>
            new StaveNote({
              keys: key ? [key] : keys,
              duration: String(duration),
            })
        );
      Formatter.FormatAndDraw(context, stave, processedNotes, {
        auto_beam: true,
      });
    });
  };

  return <div ref={container} />;
}
