import React, { useEffect, useState } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import { Score } from './Score';
import BsNav from './BsNav';
import './MusicVisualiser.css';

const MusicVisualiser = () => {
  const [noteData, setNoteData] = useState([]);
  const apiUrl = process.env.REACT_APP_API_BASE_URL;
  const fetchNotesUrl = `${apiUrl}/get_note_data?limit=16`; // Adjust accordingly

  // Function to fetch latest notes
  const fetchNotes = async () => {
    try {
      const response = await axios.get(fetchNotesUrl);
      setNoteData(response.data);
    } catch (error) {
      console.error('Error fetching note data:', error);
    }
  };

  useEffect(() => {
    fetchNotes(); // Initial fetch

    const socket = io(apiUrl);
    socket.on('data_update', () => {
      fetchNotes(); // Fetch latest notes on data update signal
    });

    return () => {
      socket.off('data_update');
      socket.disconnect();
    };
  }, [apiUrl, fetchNotesUrl]);

  return (
    <div className="MainPage-page">
      <BsNav />
      <div className="centered-content">
        <div className="score-container">
          {/* Render your notes here, e.g., using <Score> component */}
          <Score staves={noteData} />
        </div>
      </div>
    </div>
  );
};

export default MusicVisualiser;
