import React, { useState, useEffect } from 'react';
import BsNav from '../Containers/BsNav';
import { Score } from './Score';

const MusicalStairsGame = () => {
  const allTasks = [
    { name: 'Perfect 5th', notes: ['D4', 'A4'] },
    { name: 'Major 3rd', notes: ['F4', 'A4'] },
    { name: 'Perfect 4th', notes: ['B4', 'E5'] },
    { name: 'Major 2nd', notes: ['G4', 'A4'] },
    // Add other tasks as necessary
  ];

  const [completedTasks, setCompletedTasks] = useState([]);
  const [currentTask, setCurrentTask] = useState(null); // Initialize as null
  const [score, setScore] = useState(0);
  const [gameOver, setGameOver] = useState(false);

  useEffect(() => {
    generateRandomTask();
  }, []); // Empty dependency array ensures this effect runs once on mount

  const generateRandomTask = () => {
    if (completedTasks.length === allTasks.length) {
      setGameOver(true);
      return;
    }

    let remainingTasks = allTasks.filter(task => !completedTasks.includes(task));
    const randomIndex = Math.floor(Math.random() * remainingTasks.length);
    const newTask = remainingTasks[randomIndex];

    setCompletedTasks((prevTasks) => [...prevTasks, newTask]);
    setCurrentTask(newTask);
  };

  const handleTaskCompleted = () => {
    setScore(score + 1); // Assuming you want to increment score for each task completion
    generateRandomTask(); // Generate a new task after completion
  };

  const resetGame = () => {
    setCompletedTasks([]);
    setScore(0);
    setGameOver(false);
    generateRandomTask();
  };

  return (
    <div>
      <BsNav />
      <h1>Play the Musical Stairs Game</h1>
      {currentTask ? (
        <>
          <p>Play the following chord or interval on the stairs: {currentTask.name}</p>
          {/* Render the Score component with the current task's notes, ensuring notes is always an array */}
          <Score staves={[{ keys: currentTask.notes }]} clef="treble" timeSignature="4/4" />
        </>
      ) : (
        <p>Loading task...</p> // Placeholder text while no task is set
      )}
      <p>Score: {score}</p>
      {gameOver ? (
        <div>
          <p>Game Over! Your score: {score} out of {completedTasks.length}</p>
          <button onClick={resetGame}>Restart Game</button>
        </div>
      ) : (
        <button onClick={handleTaskCompleted}>Complete Task & Generate New Task</button>
      )}
    </div>
  );
};

export default MusicalStairsGame;
