//component retrieves data from mySQL through API via axios
//renders table of last 16b nptes played by the user

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/dataTable.css';
import BsNav from '../Containers/BsNav';

const DataTable = () => {
  const [data, setData] = useState([]);
  const apiUrl = process.env.REACT_APP_API_BASE_URL;

  useEffect(() => {
    if (!apiUrl) {
      console.error('REACT_APP_API_BASE_URL is not set. Ensure that the environment variable is configured.');
      return;
    }
    // Make API Request to fetch app
    axios.get(`${apiUrl}/api/get_data?limit=16`) //  'limit' query to limit results
      .then((response) => {
        // API returns the most recent notes first
        setData(response.data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, [apiUrl]);

  return (
    <div className="MainPage-page">
      <BsNav />
      <div className="data-table-container">
        <div className="data-table-content">
        <h1 style={{ textAlign: 'center' }}>Data Table</h1>
          <table className="facebook-table">
            <thead>
              <tr>
                <th>Note</th> {/* Header for numbering */}
                {/* <th>Note ID</th> */}
                <th>Time Played</th>
                <th>Pitch</th>
              </tr>
            </thead>
            <tbody>
              {data.map((item, index) => (
                <tr key={item.id}>
                  <td>{index + 1}</td> {/* Add numbering starting from 1 */}
                  {/* <td>{item.id}</td> */}
                  <td>{item.timestamp}</td>
                  <td>{item.message}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default DataTable;

// Data Fetching: The component fetches data from an API endpoint. It uses the axios library to make an HTTP GET request, 
// including a query parameter to limit the number of results returned by the API.

// Environment Variable Usage: It uses an environment variable (REACT_APP_API_BASE_URL) to determine the base URL for the API requests.
// This adds flexibility and security to the deployment environment, allowing for different base URLs in 
// different environments (e.g., development, production).

// Error Handling: The component includes basic error handling which logs errors to the console if the API request fails. 
// Additionally, it checks for the presence of the required environment variable and logs an error if it is not set.

// State Management: Uses React's useState hook to manage the data state, storing the API response data. This state is then used to dynamically render table rows.

// Conditional Rendering: Only attempts to render the table once the data is fetched. This ensures the UI is consistent with the available data.

// Component Composition: Includes a navigation bar component (BsNav), demonstrating component composition in React. 
// This helps maintain separation of concerns and enhances reusability of the BsNav component across different parts of the application.

// CSS Styling: Styles are managed through an external CSS file, promoting a clean separation of concerns between style and function.
