import React from 'react'
import axios from 'axios';


import { Score } from './Score'

function MusicVisualiser() {
  const [noteData, setNoteData] = useState([]);

  useEffect(() => {
    // Make an API request to your Flask app
    axios.get('http://localhost:5000/get_note_data')
      .then((response) => {
        setNoteData(response.data.notes);
        console.log(response.data.notes);  // Log the array to the console
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, []);


  return (
    <Score
      staves={[
        ['G3', 'd4', 'e4', 'd4'],
        ['a4', 'd4', 'e4', 'd4'],
        ['a4', 'a4', 'b4', 'a4'],
        ['d4', 'e4', ['g3', 2]],
      ]}
    />
  )
}

export default MusicVisualiser;
