import React, { useState } from 'react';
import BsNav from '../Containers/BsNav';
import axios from 'axios';
import '../styles/InstrumentSelector.css';

const InstrumentSelector = () => {
  const [activeInstrument, setActiveInstrument] = useState('Piano');
  const apiUrl = process.env.REACT_APP_API_BASE_URL;

  const switchInstrument = async (instrumentName, dictionary) => {
    console.log(`Switching instrument to: ${instrumentName}`);

    try {
      const response = await axios.post(`${apiUrl}/switch_dictionary`, {
        dictionary,
      });

      setActiveInstrument(instrumentName);
      console.log(`Switched to ${instrumentName}`, response.data);
    } catch (error) {
      console.error('Error switching instrument:', error);
    }
  };

  return (
    <div className="instrument-selector-container">
      <BsNav />
      <h2>Current Instrument: {activeInstrument}</h2>
      <div className="button-group">
        <button onClick={() => switchInstrument('Piano', 'topic_to_audio')}>Piano</button>
        <button onClick={() => switchInstrument('Guitar', 'topic_to_audio2')}>Guitar</button>
        <button onClick={() => switchInstrument('Violin', 'topic_to_audio3')}>Violin</button>
        <button onClick={() => switchInstrument('Drums', 'topic_to_audio4')}>Drums</button>
      </div>
    </div>
  );
};

export default InstrumentSelector;
