import React, { useEffect, useRef, useState } from 'react';
import { Factory, EasyScore } from 'vexflow';

const MusicVisualizer = ({ notes }) => {
  const isInitialMount = useRef(true);
  const vf = useRef(null);

  useEffect(() => {
    // Skip the effect on hot updates
    if (!isInitialMount.current) {
      return;
    }

    vf.current = new Factory({
      renderer: { elementId: 'output', width: 500, height: 200 },
    });

    const score = vf.current.EasyScore();
    const system = vf.current.System();

    system
      .addStave({
        voices: [
          score.voice(score.notes(notes.join(', '))), // Join notes array into a string
        ],
      })
      .addClef('treble')
      .addTimeSignature('4/4');

    vf.current.draw();

    isInitialMount.current = false;
  }, [notes]);

  return (
    <div>
      <h2>Music Notation</h2>
      <div id="output"></div>
    </div>
  );
};

const App = () => {
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    // Fetch notes data from Flask API endpoint
    fetch('http://localhost:5000/get_note_data')  // Replace with your actual API endpoint
      .then(response => response.json())
      .then(data => setNotes(data.notes))
      .catch(error => console.error('Error fetching notes:', error));
  }, []);
  console.log(notes)

  return (
    <div>
      <h1>Music Visualizer App</h1>
      <MusicVisualizer notes={notes} />
    </div>
  );
};

export default App;
