// integrates a WebSocketProvider for managing WebSocket connections throughout the application.

import React from 'react';
import { HashRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { WebSocketProvider } from './Components/WebSocketContext'; // Adjust the import path as necessary
import MainPage from './Components/MainPage';
import DataTable from './Components/DataTable';
import MusicVisualiser from './Components/MusicVisualiser';
import InstrumentSelector from './Components/InstrumentSelector';
import TheoryTrainer from './Components/TheoryTrainer';
import GetStartedPage from './Components/GetStartedPage'; // Ensure this is imported


function App() {
  return (
    <Router>
      <WebSocketProvider>
        <div className="app">
          <Routes>
            <Route path="/" element={<Navigate replace to="/MainPage" />} /> {/* Redirect from root to MainPage */}
            <Route path="/MainPage" element={<MainPage />} />
            <Route path="/GetStartedPage" element={<GetStartedPage />} /> {/* Ensure the path matches */}
            <Route path="/DataTable" element={<DataTable />} />
            <Route path="/MusicVisualiser" element={<MusicVisualiser />} />
            <Route path="/InstrumentSelector" element={<InstrumentSelector />} />
            <Route path="/TheoryTrainer" element={<TheoryTrainer />} />
          </Routes>
        </div>
      </WebSocketProvider>
    </Router>
  );
}

export default App;

// Router Setup:

// The application uses HashRouter (imported as Router) for client-side routing. Hash routing uses the hash (#) portion of the URL, 
// making it ideal for static websites or where clean URLs are not a necessity.

// WebSocket Context:
// The WebSocketProvider is used to wrap all child components that might need WebSocket functionality. 
// This provider is set up to handle the connection lifecycle and provide the WebSocket instance via context to any descendant component, 
// avoiding prop drilling specifically for WebSocket management.

// Routes Configuration:
// The Routes and Route components from react-router-dom are used to define the navigational structure of the application.
//  Each Route specifies a path and the component to render when the application's URL matches that path.
// A Navigate component is used on the default path ("/") to redirect users to the "/MainPage", enhancing user experience by guiding 
// them directly to the main content.
