import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/dataTable.css';
import BsNav from '../Containers/BsNav';

const DataTable = () => {
  const [data, setData] = useState([]);
  const apiUrl = process.env.REACT_APP_API_BASE_URL;

  useEffect(() => {
    if (!apiUrl) {
      console.error('REACT_APP_API_BASE_URL is not set. Ensure that the environment variable is configured.');
      return;
    }
    // Make API Request to fetch app
    axios.get(`${apiUrl}/get_data?limit=16`) // Assuming your API supports a 'limit' query to limit results
      .then((response) => {
        // Assuming the API returns the most recent notes first
        setData(response.data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, [apiUrl]);

  return (
    <div className="MainPage-page">
      <BsNav />
      <div className="data-table-container">
        <div className="data-table-content">
        <h1 style={{ textAlign: 'center' }}>Data Table</h1>
          <table className="facebook-table">
            <thead>
              <tr>
                <th>Note</th> {/* Header for numbering */}
                {/* <th>Note ID</th> */}
                <th>Time Played</th>
                <th>Pitch</th>
              </tr>
            </thead>
            <tbody>
              {data.map((item, index) => (
                <tr key={item.id}>
                  <td>{index + 1}</td> {/* Add numbering starting from 1 */}
                  {/* <td>{item.id}</td> */}
                  <td>{item.timestamp}</td>
                  <td>{item.message}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default DataTable;
