import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios'; // Import axios for making HTTP requests
import { Factory } from 'vexflow';

const MusicVisualizer = () => {
  const isInitialMount = useRef(true);
  const vf = useRef(null);
  const [noteData, setNoteData] = useState([]);

  useEffect(() => {
    // Skip the effect on hot updates
    if (!isInitialMount.current) {
      return;
    }

    // Fetch note data from your API
    axios
      .get('http://localhost:5000/get_note_data') // Use correct API endpoint
      .then((response) => {
        console.log(response.data); // Add this line

        const fetchedNoteData = response.data.notes;

        if (Array.isArray(fetchedNoteData)) {
          setNoteData(fetchedNoteData);

          // Create the VexFlow instance
          vf.current = new Factory({
            renderer: { elementId: 'output', width: 500, height: 200 },
          });

          const score = vf.current.EasyScore();
          const system = vf.current.System();

          // Assuming noteData is an array of note strings like ['C#5/q', 'B4', 'A4', 'G#4']
          system
            .addStave({
              voices: [score.voice(score.notes(...fetchedNoteData))],
            })
            .addClef('treble')
            .addTimeSignature('4/4');

          vf.current.draw();

          isInitialMount.current = false;
        } else {
          console.error('Invalid note data format:', fetchedNoteData);
        }
      })
      .catch((error) => {
        console.error('Error fetching notes:', error);
      });
  }, []);

  return (
    <div>
      <h2>Music Notation</h2>
      <div id="output"></div>
    </div>
  );
};

export default MusicVisualizer;
