import React, { useState } from 'react';
import BsNav from '../Containers/BsNav';
import axios from 'axios';  // Import axios
import '../styles/mainPage.css';

const InstrumentSelector = () => {
  const dictionary1 = process.env.DICTIONARY1;
  const dictionary2 = process.env.DICTIONARY2;
  const dictionary3 = process.env.DICTIONARY3;
  const dictionary4 = process.env.DICTIONARY4;




  const [activeDictionary, setActiveDictionary] = useState('dictionary1');
  const apiUrl = process.env.REACT_APP_API_BASE_URL;


  const switchDictionary = async (newDictionary) => {
    console.log('Switching dictionary to:', newDictionary);

    try {
      const response = await axios.post(`${apiUrl}/switch_dictionary`, {
        dictionary: newDictionary,
      });

      setActiveDictionary(newDictionary);
      console.log(`Switched to ${newDictionary}`, response.data);
    } catch (error) {
      console.error('Error switching dictionary:', error);
    }
  };

  return (
    <div>
      <BsNav />
      <h2>Current Instrument: {activeDictionary}</h2>
      <div>
        <button className="btn btn-outline-secondary" onClick={() => switchDictionary(`${dictionary1}`)}>
          Switch to Instrument 1
        </button>
      </div>
      <div>
        <button className="btn btn-outline-secondary" onClick={() => switchDictionary(`${dictionary2}`)}>
          Switch to Instrument 2
        </button>
      </div>
      <div>
        <button className="btn btn-outline-secondary" onClick={() => switchDictionary(`${dictionary3}`)}>
          Switch to Instrument 3
        </button>
      </div>
      <div>
        <button className="btn btn-outline-secondary" onClick={() => switchDictionary(`${dictionary4}`)}>
          Switch to Instrument 4
        </button>
      </div>
    </div>
  );
};

export default InstrumentSelector;
