import React, { useRef, useEffect } from 'react';
import VexFlow from 'vexflow';

const VF = VexFlow.Flow;
const { Formatter, Renderer, Stave, StaveNote } = VF;

const clefAndTimeWidth = 60;
const notesPerBar = 4; // Change this to control the number of notes per bar
const barsPerStave = 8; // Change this to control the number of bars per stave
const stavesPerLine = 8; // Change this to control the number of staves per line

export function Score({
  staves = [],
  clef = 'treble',
  timeSignature = '4/4',
  width = 1000,
  height = 150,
}) {
  const container = useRef();
  const rendererRef = useRef();

  useEffect(() => {
    if (rendererRef.current == null) {
      rendererRef.current = new Renderer(container.current, Renderer.Backends.SVG);
    }
    const renderer = rendererRef.current;
    renderer.resize(width, height);
    const context = renderer.getContext();
    context.setFont('Arial', 10, '').setBackgroundFillStyle('#eed');

    let currX = 0;
    let currY = 0;

    staves.forEach((staveNotes, i) => {
      const currentStave = new Stave(currX, currY, width - clefAndTimeWidth);
      currentStave.setWidth(width - clefAndTimeWidth);
      currentStave.addClef(clef).addTimeSignature(timeSignature);
      currentStave.setContext(context).draw();

      const processedNotes = staveNotes.map((noteWithOctave) => {
        const [note, octave] = noteWithOctave.match(/[a-zA-Z]+|[0-9]+/g);
        return new StaveNote({
          keys: [`${note}/${octave}`],
          duration: 'q', // Assuming quarter note duration
        });
      });

      Formatter.FormatAndDraw(context, currentStave, processedNotes, {
        auto_beam: true,
      });

      currX += currentStave.getWidth();
      if ((i + 1) % barsPerStave === 0) {
        currX = 0;
        currY += currentStave.getHeight();
      }
      if ((i + 1) % (stavesPerLine * barsPerStave) === 0) {
        currY = 0;
      }
    });
  }, [staves, clef, timeSignature, width, height]);

  return <div ref={container} />;
}
