    <div className="musical-stairs-game"> {/* Ensure this class aligns with your GoDaddy-like CSS */}
      <BsNav />
      <div className="game-container"> {/* Consider this for additional styling if needed */}
        <h1>Play the Musical Stairs Game</h1>
        {currentTask ? (
          <>
            <p>Play the following chord or interval on the stairs: {currentTask.name}</p>
            <button className="validate-task-btn" onClick={validateTask}>Validate Task</button>
          </>
        ) : (
          <p>Loading task...</p>
        )}
        <p>Score: {score}</p>
        {gameOver && (
          <div className="game-over-container">
            <p>Game Over! Your score: {score} out of {allTasks.length}</p>
            <button className="restart-game-btn" onClick={() => generateRandomTask()}>Restart Game</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default MusicalStairsGame;
