import React, { useState } from 'react';
import BsNav from '../Containers/BsNav';
import '../styles/mainPage.css';

const InstrumentSelector = () => {
  const [activeDictionary, setActiveDictionary] = useState('topic_to_audio');
  const switchDictionary = (newDictionary) => {
    // Make a POST request to your Flask app to switch the dictionary
    fetch('http://localhost:5000/switch_dictionary', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ dictionary: newDictionary }),
    })
      .then((response) => response.json())
      .then(() => {
        // Update the active dictionary in the React component state
        setActiveDictionary(newDictionary);
        console.log(`Switched to ${newDictionary}`);
      })
      .catch((error) => {
        console.error('Error switching dictionary:', error);
      });
  };

  return (
    <div>
      <BsNav />
      <h2>Current Instrument: {activeDictionary}</h2>
      <button onClick={() => switchDictionary('topic_to_audio')}>Switch to Instrument 1</button>
      <button onClick={() => switchDictionary('topic_to_audio2')}>Switch to Instrument 2</button>
      <button onClick={() => switchDictionary('topic_to_audio3')}>Switch to Instrument 3</button>
      <button onClick={() => switchDictionary('topic_to_audio4')}>Switch to Instrument 4</button>
    </div>
  );
};

export default InstrumentSelector;
