import React, { useRef, useEffect } from 'react';
import VexFlow from 'vexflow';

const VF = VexFlow.Flow;
const { Formatter, Renderer, Stave, StaveNote } = VF;

const clefAndTimeWidth = 60;
const barsPerLine = 8;

// ... (previous imports)

export function Score({
    staves = [],
    clef = 'treble',
    timeSignature = '4/4',
    width = 500,
    height = 150,
  }) {
    const container = useRef();
    const rendererRef = useRef();

    useEffect(() => {
      if (rendererRef.current == null) {
        rendererRef.current = new Renderer(
          container.current,
          Renderer.Backends.SVG
        );
      }

      const renderer = rendererRef.current;
      renderer.resize(width, height);
      const context = renderer.getContext();
      context.setFont('Arial', 10, '').setBackgroundFillStyle('#eed');
      const staveWidth = (width - clefAndTimeWidth) / barsPerLine;

      const drawStave = (x, notes) => {
        const stave = new Stave(x, 0, staveWidth);

        stave.setWidth(staveWidth + clefAndTimeWidth);
        stave.addClef(clef).addTimeSignature(timeSignature);
        stave.setContext(context).draw();

        const processedNotes = notes
          .map(note => (typeof note === 'string' ? { key: note } : note))
          .map(note =>
            Array.isArray(note) ? { key: note[0], duration: note[1] } : note
          )
          .map(({ key, ...rest }) =>
            typeof key === 'string'
              ? {
                  key: key.includes('/') ? key : `${key[0]}/${key.slice(1)}`,
                  ...rest,
                }
              : rest
          )
          .map(
            ({ key, keys, duration = 'q' }) =>
              new StaveNote({
                keys: key ? [key] : keys,
                duration: String(duration),
              })
          );

        const currStave = new Stave(x, 0, staveWidth);
        currStave.setContext(context);

        Formatter.FormatAndDraw(context, currStave, processedNotes, {
          auto_beam: true,
        });
      };

      for (let i = 0; i < staves.length; i += barsPerLine) {
        const notesSlice = staves.slice(i, i + barsPerLine);
        const currX = (i / barsPerLine) * staveWidth * barsPerLine;
        drawStave(currX, notesSlice);
      }
    }, [staves, clef, timeSignature, width, height]);

    return <div ref={container} />;
  }
