import React, { useEffect, useState } from 'react';
import axios from 'axios';
import io from 'socket.io-client'; // Import socket.io-client
import { Score } from './Score';
import BsNav from '../Containers/BsNav';
import '../styles/MusicVisualiser.css'; // Ensure this is the correct path

const MusicVisualiser = () => {
  const [noteData, setNoteData] = useState([]);
  const apiUrl = process.env.REACT_APP_API_BASE_URL;
  const socketUrl = process.env.REACT_APP_SOCKET_URL; // Make sure this points to your Flask-SocketIO server

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`${apiUrl}/get_note_data?limit=16`);
        setNoteData(response.data);
      } catch (error) {
        console.error('Error fetching note data:', error);
      }
    };

    fetchData(); // Fetch initial data

    // WebSocket logic
    const socket = io(socketUrl);
    socket.on('new_note_data', (newData) => {
      setNoteData(newData); // Update with real-time data
    });

    return () => {
      socket.off('new_note_data');
      socket.disconnect();
    };
  }, [apiUrl, socketUrl]);

  return (
    <div className="MainPage-page">
      <BsNav />
      <div className="centered-content">
        <div className="score-container">
          <Score className="Score" staves={noteData} />
        </div>
      </div>
    </div>
  );
};

export default MusicVisualiser;
