import React, { useRef, useEffect } from 'react';
import VexFlow from 'vexflow';

const VF = VexFlow.Flow;
const { Formatter, Renderer, Stave, StaveNote } = VF;

const Score = ({ staves }) => {
  const container = useRef();
  const rendererRef = useRef();

  useEffect(() => {
    if (rendererRef.current == null) {
      rendererRef.current = new Renderer(container.current, Renderer.Backends.SVG);
    }
    const renderer = rendererRef.current;
    renderer.resize(500, 150);
    const context = renderer.getContext();
    context.setFont('Arial', 10, '').setBackgroundFillStyle('#eed');

    staves.forEach((notes, i) => {
      const stave = new Stave(i * 100, 0, 100).addClef('treble').addTimeSignature('4/4');
      stave.setContext(context).draw();

      const processedNotes = notes.map((note) => new StaveNote({ keys: [note], duration: 'q' }));

      Formatter.FormatAndDraw(context, stave, processedNotes, {
        auto_beam: true,
      });
    });
  }, [staves]);

  return <div ref={container} />;
};

export default Score;
