import React, { useState } from 'react';
import BsNav from '../Containers/BsNav';
import axios from 'axios';
import '../styles/mainPage.css';

const InstrumentSelector = () => {
  const [activeDictionary, setActiveDictionary] = useState('topic_to_audio');

  const switchDictionary = async (newDictionary, route) => {
    console.log(`Switching dictionary to ${newDictionary} for ${route}`);

    try {
      const response = await axios.post(`http://localhost:5000/${route}`, {
        dictionary: newDictionary,
      });

      setActiveDictionary(newDictionary);
      console.log(`Switched to ${newDictionary} for ${route}`, response.data);
    } catch (error) {
      console.error(`Error switching dictionary for ${route}:`, error);
    }
  };

  return (
    <div>
      <BsNav />
      <h2>Current Instrument: {activeDictionary}</h2>
      <div>
        <button className="btn btn-outline-secondary" onClick={() => switchDictionary('topic_to_audio', 'switch_instrument1')}>
          Switch to Instrument 1
        </button>
      </div>
      <div>
        <button className="btn btn-outline-secondary" onClick={() => switchDictionary('topic_to_audio2', 'switch_instrument2')}>
          Switch to Instrument 2
        </button>
      </div>
      <div>
        <button className="btn btn-outline-secondary" onClick={() => switchDictionary('topic_to_audio3', 'switch_instrument3')}>
          Switch to Instrument 3
        </button>
      </div>
      <div>
        <button className="btn btn-outline-secondary" onClick={() => switchDictionary('topic_to_audio4', 'switch_instrument4')}>
          Switch to Instrument 4
        </button>
      </div>
    </div>
  );
};

export default InstrumentSelector;
