import React, { useEffect, useState } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import { Score } from './Score';
import BsNav from '../Containers/BsNav';
import '../styles/MusicVisualiser.css';

const MusicVisualiser = () => {
  const [noteData, setNoteData] = useState([]);
  const [refreshKey, setRefreshKey] = useState(0); // A key to force re-render
  const apiUrl = process.env.REACT_APP_API_BASE_URL;
  const wsUrl = process.env.REACT_APP_WS_URL; 

  // Function to fetch note data
  const fetchNotes = async () => {
    try {
      const response = await axios.get(`${apiUrl}/get_note_data?limit=16`);
      setNoteData(response.data);
      console.log('Note data fetched');
    } catch (error) {
      console.error('Error fetching note data:', error);
    }
  };

  useEffect(() => {
    fetchNotes(); // Fetch initial note data

    // WebSocket connection setup
    const socket = io(wsUrl);
    socket.on('connect', () => {
      console.log('Connected to WebSocket server');
    });

    // Listen for the specific message to re-render the component
    socket.on('data_update', () => {
      console.log('data update received, refreshing component');
      setRefreshKey(prevKey => prevKey + 1); // Update the key to force re-render
      // window.location.reload();
      // fetchNotes(); // Optionally fetch the latest notes again
    });

    return () => {
      socket.disconnect(); // Clean up on component unmount
    };
  }, [refreshKey]); // Depend on refreshKey to re-run the effect when it changes

  return (
    <div key={refreshKey} className="MainPage-page">
      <BsNav />
      <div className="centered-content">
        <div className="score-container">
          <p className="instruction-text">Here is a real-time score generator. Step on the musical stairs to see what notes you have played!</p>
          <Score className="Score" staves={noteData} />
        </div>
      </div>
    </div>
  );
};

export default MusicVisualiser;
