import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { getSocket } from './SocketService'; // Adjust the import path as necessary
import { Score } from './Score';
import BsNav from '../Containers/BsNav';
import '../styles/MusicVisualiser.css';

const MusicVisualiser = () => {
  const [noteData, setNoteData] = useState([]);
  const [dataUpdateTrigger, setDataUpdateTrigger] = useState(false); // Trigger state
  const apiUrl = process.env.REACT_APP_API_BASE_URL;

  // Function to fetch the latest note data
  const fetchNotes = async () => {
    try {
      const response = await axios.get(`${apiUrl}/get_note_data?limit=16`);
      setNoteData(response.data);
      console.log('Note data fetched');
    } catch (error) {
      console.error('Error fetching note data:', error);
    }
  };

  useEffect(() => {
    fetchNotes(); // Fetch initial note data
  }, [apiUrl, dataUpdateTrigger]); // Re-fetch notes when dataUpdateTrigger changes

  useEffect(() => {
    const socket = getSocket(); // Use the singleton socket instance

    // Listen for the signal indicating new data is available
    socket.on('data_update', () => {
      console.log('New data available signal received');
      setDataUpdateTrigger(prev => !prev); // Toggle the trigger to re-fetch notes
    });

    // Clean up by removing the event listener
    return () => {
      socket.off('data_update');
    };
  }, []);

  return (
    <div className="MainPage-page">
      <BsNav />
      <div className="centered-content">
        <div className="score-container">
          <Score className="Score" staves={noteData} />
        </div>
      </div>
    </div>
  );
};

export default MusicVisualiser;
