import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Score } from './Score';

function MusicVisualiser() {
  const [noteData, setNoteData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/get_note_data');
        setNoteData(response.data);
        console.log('Note data fetched:', response.data);
      } catch (error) {
        console.error('Error fetching note data:', error);
      }
    };

    fetchData(); // Call the fetchData function when the component mounts

    // Optionally, you can include dependencies in the dependency array of useEffect
    // if you want to re-run the effect when certain values change.
    // For example, if you want to fetch data again when some prop changes:
    // const fetchData = async () => { ... };
    // fetchData();
  }, []); // The empty dependency array ensures that this effect runs only once on mount

  return (
    <div>
      <h2>Music Notation</h2>
      <Score staves={noteData} />
    </div>
  );
}

export default MusicVisualiser;
